import "./App.css"
import Header from './header/Header'

function App() {

 function boredApi(){
    fetch(`https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&ids=`+selectSymbol+`&order=market_cap_desc&per_page=100&page=1&sparkline=true`)
    .then(response => response.json())
    .then(data => [setSymbol(data[0].id),setPrice(data[0]['sparkline_in_7d'].price.slice(-1)[0])])
  }
  const [selectSymbol,setSelectSymbol] =  useState("ethereum")
  const[symbol,setSymbol]=useState("")
  const[price,setPrice]=useState("")
  useEffect(()=>{
    console.log('fetched Activity')
  },[selectSymbol]) 
  return (
    <>
      <Header/>
      <div className='main-container' className>
        <h1>Crypto Market</h1>
        <div className='select-crypto'>
          <select onChange={(e)=>{
            const selectedSymbol = e.target.value;
            setSelectSymbol(selectedSymbol)
          }}>
            <option value="ethereum">Ethereum</option>
            <option value="bitcoin">Bitcoin</option>
          </select>
        </div>
        <button onClick={boredApi} className='price-btn'>Get Latest Price</button>
        <p className='results'>Name: {symbol.toLocaleUpperCase()}</p>
        <p className='results'>Price: {Math.round(price)} USD</p>
      </div>
    </>
  )
}

export default App
