import React, { useEffect, useState } from 'react'

function App() {

 function boredApi(){
    fetch('https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&ids=ethereum&order=market_cap_desc&per_page=100&page=1&sparkline=true')
    .then(response => response.json())
    .then(data => [setSymbol(data[0].id),setPrice(data[0]['sparkline_in_7d'].price.slice(-1)[0])])
  }
  const[symbol,setSymbol]=useState("")
  const[price,setPrice]=useState("")
  useEffect(()=>{
    console.log('fetched Activity')

  },[symbol]) 
  return (
    <div>
      <h1>Crypto Market</h1>
      <select>
        <option value=""></option>
      </select>
      <button onClick={boredApi}>Get Latest Price</button>
      <p>Name: {symbol}</p>
      <p>Price: {price}</p>
    </div>
  )
}

export default App
